package PersonalFinanceManagerWeek2;import javax.swing.*;import java.awt.*;import java.awt.event.*;// Class to set a new financial goalpublic class SetGoals extends JFrame implements ActionListener {        // Components for goal setting    private Choice cGoalType;    private JTextField tfTargetAmount, tfStartingBalance, tfDeadline;    private JButton save, clear, back;    private String username;    // Define Dark Theme Colors    private static final Color BG_DARK = new Color(25, 25, 25);    private static final Color PANEL_DARK = new Color(40, 40, 40);    private static final Color TEXT_LIGHT = Color.WHITE;    private static final Color ACCENT_GREEN = new Color(0, 180, 0); // Save Button    private static final Color ACCENT_BLUE = new Color(30, 100, 255); // Clear Button    private static final Color FIELD_BG = new Color(50, 50, 50);    SetGoals(String username) {        this.username = username;        // --- Frame Setup ---        setTitle("SET NEW FINANCIAL GOAL");        setBounds(500, 200, 700, 480);        setLocationRelativeTo(null);        getContentPane().setBackground(BG_DARK);        setLayout(null);         // --- Heading ---        JLabel text = new JLabel("DEFINE YOUR SAVINGS GOAL");        text.setFont(new Font("Tahoma", Font.BOLD, 22));        text.setForeground(ACCENT_GREEN);        text.setBounds(70, 20, 400, 30);        add(text);        // --- Goal Input Fields (Centered Layout) ---        int labelX = 100;        int valueX = 350;        int yStart = 80;        int ySpacing = 40;                // 1. User Account (Display only)        JLabel lblUsername = createLabel("User Account", labelX, yStart);        add(lblUsername);                JLabel labelUsername = createValueLabel(username, valueX, yStart);        add(labelUsername);        yStart += ySpacing;                // 2. Goal Type        JLabel lblGoalType = createLabel("Goal Type / Name", labelX, yStart);        add(lblGoalType);                cGoalType = new Choice();        cGoalType.add("Emergency Fund");        cGoalType.add("Dream Vacation");        cGoalType.add("House Down Payment");        cGoalType.add("Investment Capital");        cGoalType.setBounds(valueX, yStart, 200, 25);        cGoalType.setBackground(FIELD_BG);        cGoalType.setForeground(TEXT_LIGHT);        add(cGoalType);        yStart += ySpacing;        // 3. Target Amount        JLabel lblTargetAmount = createLabel("Target Amount (Rs)", labelX, yStart);        add(lblTargetAmount);                tfTargetAmount = createTextField("0.00", valueX, yStart);        add(tfTargetAmount);        yStart += ySpacing;        // 4. Starting Balance        JLabel lblStartingBalance = createLabel("Current/Starting Balance (Rs)", labelX, yStart);        add(lblStartingBalance);                tfStartingBalance = createTextField("0.00", valueX, yStart);        add(tfStartingBalance);        yStart += ySpacing;                // 5. Deadline        JLabel lblDeadline = createLabel("Target Deadline (Months)", labelX, yStart);        add(lblDeadline);                tfDeadline = createTextField("12", valueX, yStart);        add(tfDeadline);        yStart += ySpacing + 30;        // --- Buttons ---        int buttonXStart = 100;        int btnWidth = 140;        int btnGap = 20;        // Save Button        save = new JButton("Save Goal");        save.setBackground(ACCENT_GREEN);        save.setForeground(TEXT_LIGHT);        save.setBounds(buttonXStart, yStart, btnWidth, 35);        save.addActionListener(this);        add(save);                // Clear Button        clear = new JButton("Clear Fields");        clear.setBackground(ACCENT_BLUE);        clear.setForeground(TEXT_LIGHT);        clear.setBounds(buttonXStart + btnWidth + btnGap, yStart, btnWidth, 35);        clear.addActionListener(this);        add(clear);        // Back Button        back = new JButton("Back");        back.setBackground(Color.DARK_GRAY);        back.setForeground(TEXT_LIGHT);        back.setBounds(buttonXStart + 2 * (btnWidth + btnGap), yStart, btnWidth - 40, 35);        back.addActionListener(this);        add(back);                setVisible(true);    }        // --- Helper Methods ---    private JLabel createLabel(String text, int x, int y) {        JLabel label = new JLabel(text);        label.setFont(new Font("Tahoma", Font.PLAIN, 16));        label.setBounds(x, y, 250, 25);        label.setForeground(TEXT_LIGHT);        return label;    }    private JLabel createValueLabel(String text, int x, int y) {        JLabel label = new JLabel(text);        label.setFont(new Font("Tahoma", Font.BOLD, 16));        label.setBounds(x, y, 200, 25);        label.setForeground(TEXT_LIGHT);        return label;    }        private JTextField createTextField(String defaultText, int x, int y) {        JTextField tf = new JTextField(defaultText);        tf.setBounds(x, y, 200, 25);        tf.setBackground(FIELD_BG);        tf.setForeground(ACCENT_GREEN);        tf.setCaretColor(TEXT_LIGHT);        return tf;    }    // --- Action Listener ---    public void actionPerformed(ActionEvent ae) {        if (ae.getSource() == save) {            try {                String goalType = cGoalType.getSelectedItem();                double target = Double.parseDouble(tfTargetAmount.getText().replace(",", ""));                double start = Double.parseDouble(tfStartingBalance.getText().replace(",", ""));                int deadline = Integer.parseInt(tfDeadline.getText());                                if (target <= start) {                    JOptionPane.showMessageDialog(this, "Target Amount must be greater than Starting Balance.", "Input Error", JOptionPane.ERROR_MESSAGE);                    return;                }                                if (deadline <= 0) {                     JOptionPane.showMessageDialog(this, "Deadline must be a positive number of months.", "Input Error", JOptionPane.ERROR_MESSAGE);                    return;                }                // Placeholder for Database Insertion                String message = String.format("Goal '%s' saved! Target: Rs %,.0f. Monthly needed: Rs %,.0f.",                                                goalType, target, (target - start) / deadline);                                JOptionPane.showMessageDialog(this, message, "Goal Saved Successfully", JOptionPane.INFORMATION_MESSAGE);                setVisible(false);                dispose();                            } catch (NumberFormatException e) {                JOptionPane.showMessageDialog(this, "Please enter valid numbers for amounts and deadline.", "Input Error", JOptionPane.ERROR_MESSAGE);            }        } else if (ae.getSource() == clear) {            cGoalType.select(0);            tfTargetAmount.setText("0.00");            tfStartingBalance.setText("0.00");            tfDeadline.setText("12");        } else if (ae.getSource() == back) {            setVisible(false);            dispose();        }    }        public static void main(String[] args) {        new SetGoals("sarmad");    }}