package PersonalFinanceManagerWeek2;import javax.swing.*;import java.awt.*;import java.awt.event.*;// This class must be saved in a file named ViewBudgets.javapublic class ViewBudgets extends JFrame implements ActionListener {    JButton back;    // Define Dark Theme Colors    private static final Color BG_DARK = new Color(25, 25, 25);    private static final Color PANEL_DARK = new Color(40, 40, 40);    private static final Color TEXT_LIGHT = Color.WHITE;    private static final Color ACCENT_BLUE = new Color(30, 100, 255);    private static final Color WARNING_ORANGE = new Color(255, 150, 0); // For limit/high spend    private static final Color PRIMARY_VALUE = new Color(0, 200, 0);    // Green for positive/remaining    private static final Color LOW_CONTRAST_TEXT = Color.GRAY; // For placeholder text    ViewBudgets(String username) {        // --- Frame Setup ---        setTitle("View Budget Status");        setBounds(450, 200, 900, 450);        setLocationRelativeTo(null);        getContentPane().setBackground(BG_DARK);        setLayout(null);        // ðŸš¨ FIX APPLIED HERE: Change close operation to DISPOSE_ON_CLOSE        setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);        // --- 1. Left Panel (Details, Labels, and Back Button) ---        JPanel p1 = new JPanel();        p1.setBackground(PANEL_DARK);        p1.setBounds(20, 20, 400, 390); // Left Panel        p1.setLayout(null);        add(p1);        // Heading        JLabel text = new JLabel("VIEW CURRENT BUDGET STATUS");        text.setFont(new Font("Tahoma", Font.BOLD, 18));        text.setForeground(TEXT_LIGHT);        text.setBounds(50, 10, 350, 30);        p1.add(text);        // --- GUI Components Setup (Budget Details) ---        int labelX = 30;        int valueX = 220;        int yStart = 60;        int ySpacing = 35;        // 1. User        JLabel lblusername = createLabel("User Account", labelX, yStart);        p1.add(lblusername);        JLabel labelusername = createValueLabel(username, valueX, yStart, TEXT_LIGHT);        p1.add(labelusername);        yStart += ySpacing;        // 2. Budget Category        JLabel lblCategory = createLabel("Budget Category", labelX, yStart);        p1.add(lblCategory);        JLabel labelCategory = createValueLabel("Groceries", valueX, yStart, TEXT_LIGHT); // Dummy Data        p1.add(labelCategory);        yStart += ySpacing;        // 3. Target Limit        JLabel lblLimit = createLabel("Target Limit (Rs)", labelX, yStart);        p1.add(lblLimit);        JLabel labelLimit = createValueLabel("Rs 15,000", valueX, yStart, WARNING_ORANGE, true); // Dummy Data, Bold        p1.add(labelLimit);        yStart += ySpacing;        // 4. Budget Period        JLabel lblPeriod = createLabel("Budget Period", labelX, yStart);        p1.add(lblPeriod);        JLabel labelPeriod = createValueLabel("November 2025", valueX, yStart, TEXT_LIGHT); // Dummy Data        p1.add(labelPeriod);        yStart += ySpacing;        // 5. Current Spend        JLabel lblCurrentSpend = createLabel("Current Spend (Rs)", labelX, yStart);        p1.add(lblCurrentSpend);        JLabel labelCurrentSpend = createValueLabel("Rs 12,500", valueX, yStart, WARNING_ORANGE, true); // Dummy Data, Bold        p1.add(labelCurrentSpend);        yStart += ySpacing;        // 6. Remaining Balance        JLabel lblRemaining = createLabel("Remaining Balance", labelX, yStart);        p1.add(lblRemaining);        JLabel labelRemaining = createValueLabel("Rs 2,500", valueX, yStart, PRIMARY_VALUE, true); // Dummy Data, Green, Bold        p1.add(labelRemaining);        yStart += ySpacing;        // 7. Spending Pace        JLabel lblPace = createLabel("Spending Pace", labelX, yStart);        p1.add(lblPace);        JLabel labelPace = createValueLabel("On Track (83%)", valueX, yStart, TEXT_LIGHT); // Dummy Data        p1.add(labelPace);        yStart += ySpacing + 20;        // Note: Progress bar code removed from p1 here.        // --- Back Button ---        back = new JButton("Back");        back.setBackground(ACCENT_BLUE);        back.setForeground(Color.WHITE);        back.setBounds(150, 340, 100, 35); // Keep centered in p1        back.setFont(new Font("Tahoma", Font.BOLD, 14));        back.addActionListener(this);        p1.add(back);        // --- 2. Right Panel (Progress Bar and Placeholder) ---        JPanel p2 = new JPanel();        p2.setBackground(BG_DARK);        p2.setBounds(440, 20, 440, 400);        p2.setLayout(null); // Use null layout for precise placement        add(p2);        // --- Progress Bar (Moved from p1) ---        JProgressBar progressBar = new JProgressBar(0, 15000); // Max limit 15000        progressBar.setValue(12500); // Current spend 12500        // Centered within the right panel (440px wide) and placed near the top        progressBar.setBounds(30, 100, 380, 40); // Increased height for visibility        progressBar.setStringPainted(true);        progressBar.setForeground(PRIMARY_VALUE);        progressBar.setBackground(PANEL_DARK);        p2.add(progressBar);        // --- Budget Visualization Placeholder (Modified to sit below the bar) ---        JLabel placeholder = new JLabel("Budget Visualization Placeholder");        placeholder.setForeground(LOW_CONTRAST_TEXT);        placeholder.setFont(new Font("Tahoma", Font.ITALIC, 16));        placeholder.setBounds(90, 200, 300, 30); // Center placeholder below the bar        p2.add(placeholder);        setVisible(true);    }    // Helper method to create standard labels    private JLabel createLabel(String text, int x, int y) {        JLabel label = new JLabel(text);        label.setBounds(x, y, 150, 25);        label.setForeground(TEXT_LIGHT);        return label;    }    // Helper method to create value labels    private JLabel createValueLabel(String text, int x, int y, Color color) {        return createValueLabel(text, x, y, color, false);    }    private JLabel createValueLabel(String text, int x, int y, Color color, boolean isBold) {        JLabel label = new JLabel(text);        label.setBounds(x, y, 150, 25);        label.setForeground(color);        if (isBold) {            label.setFont(new Font("Tahoma", Font.BOLD, 14));        } else {            label.setFont(new Font("Tahoma", Font.PLAIN, 12));        }        return label;    }    public void actionPerformed(ActionEvent ae) {        if (ae.getSource() == back) {            // Both setVisible(false) and dispose() are necessary to properly close the window            setVisible(false);            dispose();         }    }    public static void main(String[] args) {                new ViewBudgets("sarmad");    }}